<?php get_header(); ?><div id="content"><?php include("headlineArea.php"); ?> <?php if (have_posts()) : while (have_posts()) : the_post(); ?>       <div class="contenttitle">   <h1><a href="<?php the_permalink() ?>" rel="bookmark" title="<?php the_title(); ?>"><?php the_title(); ?></a></h1>  </div>     <div style="clear:both;"></div>   <?php the_content();?>   <center><div class="afterpost"></div></center>  <?php trackback_rdf(); ?>      <?php endwhile; else: ?>      <p><?php _e('Sorry, no posts matched your criteria.'); ?></p><?php endif; ?>       <div class="navigation">     <div class="previous"><?php next_posts_link('&laquo; Previous Entries') ?></div>    <div class="next"><?php previous_posts_link('Next Entries &raquo;') ?></div>   </div>      </div>  <?php get_sidebar(); ?>   </div><!-- The main column ends  --><?php get_footer(); ?>